#include <stdio.h>
#include <iostream>
#include <string>

using namespace std;

string add(string a, string b){
    int len_a = a.size() - 1;
    int len_b = b.size() - 1;

    string total = "";

    int sum = 0;
    while(len_a >= 0 || len_b >= 0){
        if(len_b >= 0)  sum += (int)b[len_b--] - '0';
        if(len_a >= 0)  sum += (int)a[len_a--] - '0';

        total.insert(total.begin(), (sum % 10) + '0');
        sum /= 10;
    }
    if(sum) total.insert(total.begin(), sum + '0');
    return total;
}

